    private void BtnSignUpActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnSignUpActionPerformed
    String adminID = txtAdminID.getText();  // Get Admin ID from input field 
String name = txtAdminName.getText();
String password = new String(txtPassword.getPassword());  // Get password from input field
String confirmPassword = new String(txtConfirmPassword.getPassword());  // Get confirm password from input field

// Validate input fields
if (adminID.isEmpty() || name.isEmpty() || password.isEmpty() || confirmPassword.isEmpty()) {
    JOptionPane.showMessageDialog(this, "All fields are required!");
    return;
}

if (!password.equals(confirmPassword)) {
    JOptionPane.showMessageDialog(this, "Passwords do not match!");
    return;
}

String queryInsert = "INSERT INTO admin (adminid, name, password) VALUES (?, ?, ?)";

try (Connection conn = DBconnection.connect();
     PreparedStatement insertStmt = conn.prepareStatement(queryInsert)) {

    insertStmt.setString(1, adminID);
    insertStmt.setString(2, name);
    insertStmt.setString(3, password);
    int rowsInserted = insertStmt.executeUpdate();

    if (rowsInserted > 0) {
        JOptionPane.showMessageDialog(this, "Signup Successful!");

        // âœ… Clear all fields after successful signup
        txtAdminID.setText("");
        txtAdminName.setText("");
        txtPassword.setText("");
        txtConfirmPassword.setText("");
    } else {
        JOptionPane.showMessageDialog(this, "Signup Failed!");
    }
} catch (SQLException ex) {
    JOptionPane.showMessageDialog(this, "Database Error: " + ex.getMessage());
}

    }//GEN-LAST:event_BtnSignUpActionPerformed

